sys_variable_value.value:
(function execute(inputs, outputs) {

  try{
    var request  = new sn_ws.RESTMessageV2();        
    request.setHttpMethod('post');

    var attachment = new GlideSysAttachment();
    var agr = attachment.getAttachments(inputs.tableName, inputs.recordId);
    if(agr.next()){
    var base64Attachment = attachment.getContentBase64(agr);
  }

    var data  = {
      api_key: inputs.apiKey,
      images: base64Attachment.split(),
      modifiers: ["crops_fast", "similar_images"],
      plant_language: "de",
      plant_details: ["common_names",
                      "url",
                      "name_authority",
                      ]
    };

    //endpoint - ServiceNow REST Attachment API        
    request.setEndpoint('https://api.plant.id/v2/identify');   
    request.setRequestHeader("Content-Type","application/json");
	request.setRequestBody(JSON.stringify(data));

    var response = request.execute();        
    var httpResponseStatus = response.getStatusCode();  

    var responseObj = JSON.parse(response.getBody());

    outputs.plant_name = responseObj.suggestions[0].plant_name
    outputs.common_name = responseObj.suggestions[0].plant_details.common_names[0]
  }
  catch(ex){
    var message  = ex.getMessage();        
    gs.info(message);    
  }



})(inputs, outputs);

